        }
    static String  sortString(String str){

        String[] string = str.split(" ");
        List<Integer> integer = new ArrayList<>();
        for (int i = 0; i < string.length; i ++) {
            integer.add(Integer.parseInt(string[i]));
        }
        sort(integer);
